pragma solidity ^0.8.12;

import "@openzeppelin/contracts/utils/math/SafeMath.sol";

library SportsBettingLib {
    // Define DEFAULT FixtureResult = 0. 
    // DEFAULT FixtureResult is actually invalid and acts a placeholder to catch erroneous
    // FixtureResult entries, as Solidity interprets null values as 0.
    // CANCELLED FixtureResult allows us to handle cases where sports fixtures are cancelled
    // and we should allow all stakers to withdraw their stakes
    enum FixtureResult {
        DEFAULT,
        CANCELLED,
        HOME,
        DRAW,
        AWAY
    }

    function getFixtureResultFromAPIResponse(
        uint256 result
    ) external pure returns (FixtureResult) {
        if (result == uint256(FixtureResult.HOME)) {
            return FixtureResult.HOME;
        } else if (result == uint256(FixtureResult.DRAW)) {
            return FixtureResult.DRAW;
        } else if (result == uint256(FixtureResult.AWAY)) {
            return FixtureResult.AWAY;
        } else if (result == uint256(FixtureResult.CANCELLED)) {
            return FixtureResult.CANCELLED;
        }
        return FixtureResult.DEFAULT;
    }

    function getLosingFixtureOutcomes(FixtureResult winningOutcome)
        external
        pure
        returns (FixtureResult[] memory)
    {
        FixtureResult[] memory losingOutcomes = new FixtureResult[](2);

        uint256 losingOutcomesIndex = 0;
        for (uint256 i = uint256(FixtureResult.HOME); i <= uint256(FixtureResult.AWAY); i++) {
            if (FixtureResult(i) != winningOutcome) {
                losingOutcomes[losingOutcomesIndex] = FixtureResult(i);
                losingOutcomesIndex += 1;
            }
        }
        return losingOutcomes;
    }

    function calculateStakerObligation(
        uint256 stakerAmount,
        uint256 winningAmount,
        uint256 totalAmount
    ) public pure returns(uint256) {
        bool flag;
        uint256 stakerShare;
        uint256 obligation;
        (flag, stakerShare) = SafeMath.tryMul(totalAmount, stakerAmount);
        if (!flag) {
            revert("Overflow calculating obligation");
        }
        (flag, obligation) = SafeMath.tryDiv(stakerShare, winningAmount);
        if (!flag) {
            revert("Division by zero");
        }
        
        return obligation;
    }

    function calculateCommission(
        uint256 stakerObligation,
        uint256 stakerAmount,
        uint256 commissionRate
    ) public pure returns(uint256) {
        bool flag;
        uint256 profit;
        uint256 commission;
        (flag, profit) = SafeMath.trySub(stakerObligation, stakerAmount);
        if (!flag) {
            revert("Underflow calculating profit");
        }
        (flag, commission) = SafeMath.tryMul(commissionRate, profit);
        if (!flag) {
            revert("Overflow calculating commission");
        }
        // Divide by 100 as COMMISSION_RATE is in percentage terms
        return commission / 100;
    }
}","keccak256":"0xc4b3cb91f383911078b89c243dc5cdc353d4385c9e9e7b69b1cc0d4ddd25c906","license":"Unlicense"},"contracts/SportsOracleConsumer.sol":{"content":"//SPDX-License-Identifier: MIT
pragma solidity ^0.8.12;

import "@chainlink/contracts/src/v0.8/ChainlinkClient.sol";
//import "./mock/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

/**
 * Request testnet LINK and ETH here: https://faucets.chain.link/
 * Find information on LINK Token Contracts and get the latest ETH and LINK faucets here: https://docs.chain.link/docs/link-token-contracts/
 */

abstract contract SportsOracleConsumer is ChainlinkClient {
    using Chainlink for Chainlink.Request;

    address public immutable chainlink;
    string public sportsOracleURI;

    bytes32 private jobId;
    uint256 private fee;

    mapping(address => uint256) public userToLink;

    event RequestedFixtureKickoff(bytes32 indexed requestId, string fixtureID);

    event RequestedFixtureResult(bytes32 indexed requestId, string fixtureID);

    event RequestFixtureKickoffFulfilled(
        bytes32 indexed requestId,
        string fixtureID,
        uint256 kickoff
    );

    event RequestFixtureResultFulfilled(
        bytes32 indexed requestId,
        string fixtureID,
        uint256 result
    );

    event RequestFixtureResultError(
        bytes32 indexed requestId,
        string error
    );
    
    constructor(
        string memory _sportsOracleURI,
        address _oracle,
        address _link,
        string memory _jobId,
        uint256 _fee
    ) {
        sportsOracleURI = _sportsOracleURI;
        setChainlinkToken(_link);
        setChainlinkOracle(_oracle);
        chainlink = _oracle;
        jobId = stringToBytes32(_jobId);
        fee = _fee;
    }

    modifier hasLinkFee() {
        require(userToLink[msg.sender] >= fee, "You haven't sent enough LINK.");
        _;
    }

    function stringToBytes32(string memory source)
        private
        pure
        returns (bytes32 result)
    {
        bytes memory tempEmptyStringTest = bytes(source);
        if (tempEmptyStringTest.length == 0) {
            return 0x0;
        }

        assembly {
            // solhint-disable-line no-inline-assembly
            result := mload(add(source, 32))
        }
    }

    /**
     * @notice Request fixture kickoff time from the oracle in a single transaction
     */
    function requestFixtureKickoffTimeParameter(string memory fixtureID)
        public
        hasLinkFee
        returns (bytes32)
    {
        // User spends LINK value = fee on this request
        userToLink[msg.sender] -= fee;

        Chainlink.Request memory req = buildChainlinkRequest(
            jobId,
            address(this),
            this.rawFulfillFixtureKickoffTime.selector
        );
        req.add("get", string.concat(sportsOracleURI, fixtureID));
        req.add("path", "0,ko");
        req.addInt("times", 1);
        return sendChainlinkRequest(req, fee); // MWR API.
    }

    function rawFulfillFixtureKickoffTime(bytes32 requestId, uint256 ko)
        public
        recordChainlinkFulfillment(requestId)
    {
        require(msg.sender == chainlink, "Only ChainlinkClient can fulfill");
        fulfillFixtureKickoffTime(requestId, ko);
    }

    function fulfillFixtureKickoffTime(bytes32 requestId, uint256 ko)
        internal
        virtual;

    /**
     * @notice Request fixture result from the oracle in a single transaction
     */
    function requestFixtureResultParameter(string memory fixtureID)
        public
        hasLinkFee
        returns (bytes32)
    {
        // User spends LINK value = fee on this request
        userToLink[msg.sender] -= fee;

        Chainlink.Request memory req = buildChainlinkRequest(
            jobId,
            address(this),
            this.rawFulfillFixtureResult.selector
        );
        req.add("get", string.concat(sportsOracleURI, fixtureID));
        req.add("path", "0,result");
        req.addInt("times", 1);
        return sendChainlinkRequest(req, fee); // MWR API.
    }

    function rawFulfillFixtureResult(bytes32 requestId, uint256 result)
        public
        recordChainlinkFulfillment(requestId)
    {
        require(msg.sender == chainlink, "Only ChainlinkClient can fulfill");
        fulfillFixtureResult(requestId, result);
    }

    function fulfillFixtureResult(bytes32 requestId, uint256 result)
        internal
        virtual;

    // Anybody can transfer LINK to ctx
    function transferLink(uint256 amount) public {
        IERC20 link = IERC20(chainlinkTokenAddress());
        require(
            link.transferFrom(msg.sender, address(this), amount),
            "Unable to transfer"
        );

        userToLink[msg.sender] += amount;
    }

    /**
     * Allow withdraw of Link tokens from the contract
     */
    function withdrawLink(uint256 amount) public {
        require(amount <= userToLink[msg.sender], "You don't have enough link");
        userToLink[msg.sender] -= amount;

        IERC20 link = IERC20(chainlinkTokenAddress());
        require(link.transfer(msg.sender, amount), "Unable to transfer");
    }
}
